    private void btnAddMagazineActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddMagazineActionPerformed
        // variables and read text boxes 
        String title = txtTitle.getText(); 
        String subject = txtSubject.getText(); 
        String audience = txtAudience.getText(); 
        float price = Float.parseFloat(txtPrice.getText()); 
        
        // instantiate our magazine object variable - custom constructor approach 
        // Make Sure arguments passed here match the constructor parameter list in Magazine.Java
        // I like this way better
        Magazine myMagazine = new Magazine(title, subject, price, audience); 
        
        //display the magazine info on the list 
        lstMagazine.add(myMagazine.toString()); 
        
        
        
        
        
        
        
        
        
        
        
        
        
    }//GEN-LAST:event_btnAddMagazineActionPerformed

