    private void btnAddItemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddItemActionPerformed
        // array of prices 
        float prices[] = { 5.00f, 4.00f, 6.00f, 3.00f }; 
        
        // local variables and read the user input 
        String name = txtName.getText(); 
        int orderNo = Integer.parseInt(txtOrderNo.getText()); 
        int index = cboType.getSelectedIndex(); 
        String burritoType = cboType.getSelectedItem().toString(); 
        float price = prices[index]; 
        int qty = Integer.parseInt(txtQty.getText()); 
        
        // header from custom Burrito Constructor pasted in 
        // be sure no of argumenets and burrito type match
       //  public Burrito(String customerName, int orderNo, String burritoType, int qty, float price); 
         Burrito myBurrito = new Burrito( name, orderNo, burritoType, price, qty ); 
        
        lstOrder.add(myBurrito.toString()); 
        
        
        
        
        
        
        
        
    }//GEN-LAST:event_btnAddItemActionPerformed

