    private void btnCarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCarActionPerformed
        // variables 
        float prices[] = {18500, 35000, 40000, 50000}; 
        
        // read the control values into variables. Doesnt really matter what order as long as we get them all
           int index = cboModel.getSelectedIndex(); // gets index if item is selected 
           String model = cboModel.getSelectedItem().toString(); // gets the combo selected item text
           
           String make = txtMake.getText(); 
           String color = txtColor.getText(); 
           String vin = txtVin.getText(); 
           Boolean isTopDown = chkIsTopDown.isSelected(); 
           
           
           // combo box selected index >= 0; -1 if nothing selected 
      if (index >= 0) {
          // get the price from our parellel array (hint for lab 6)!!!!!!!!!!!!!!!! 
          float price = prices[index]; 
          
           // create the right object 
           if (index == 1) {
                // convertible 
               // create using the custom constructor 
               Convertible myCar = new Convertible(isTopDown, vin, make, model, color, price); 
               
               
               // retrieve the car info 
               lblCarInfo.setText(myCar.getCarInfo()); 
           }
           else {
               // some other one - create a car object 
               Car myCar = new Car(vin, make, model, color, price); 
               // retrieve the car info 
               lblCarInfo.setText(myCar.getCarInfo()); 
               
           }
       } 
           
    }//GEN-LAST:event_btnCarActionPerformed

