    private void btnFootballActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFootballActionPerformed
        // TODO: read the text boxes into variables 
        String name = txtName.getText(); 
        int number = Integer.parseInt(txtNumber.getText()); 
        String Position = txtPosition.getText(); 
        int gamesPlayed = Integer.parseInt(txtCombo.getText()); 
        
        // create a football player object using the default constructor
        FootballPlayer myPlayer = new FootballPlayer(); 
                
        // make sure we can set all of the properties 
        myPlayer.setName(name);
        myPlayer.setNumber(number);
        myPlayer.setPosition(Position); 
        myPlayer.setGamesPlayed(gamesPlayed); 
        
        
        // add the player to the list and make sure that we can get all of the player info
        lstPlayer.add(myPlayer.getPlayerInfo()); 
        
        
        
    }//GEN-LAST:event_btnFootballActionPerformed

    private void btnBBallActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBBallActionPerformed
       
        // TODO: read the text boxes into variables 
        String name = txtName.getText(); 
        int number = Integer.parseInt(txtNumber.getText()); 
        String Position = txtPosition.getText(); 
        float freeThrowPct = Float.parseFloat(txtCombo.getText());  
        
        
        
        // create our object using the custom constructor 
        // BasketballPlayer(String position, float freeThrowPct, int number, String name) 
        // tip: paste the constructor header to make it easier to match argunments 
        BasketballPlayer myPlayer = new BasketballPlayer(Position, freeThrowPct, number, name);  
         
         // add the player to the list and make sure that we can get all of the player info
        lstPlayer.add(myPlayer.getPlayerInfo());  
        
    }//GEN-LAST:event_btnBBallActionPerformed

